var sv, ScriptsVari = {
    settings: {
        $body: $("body"),
        $html: $("html")
    },
    init: function() {
        sv = this.settings, this.bindUIActions(), this.onLoadActions(), this.scrollActions(), 
        this.resizeActions(), this.ieMessage();
    },
    onLoadActions: function() {
        $(window).load(function() {});
    },
    bindUIActions: function() {
        var i = $("#datetimepicker1").find("input").data("limit");
        $("#datetimepicker1").datetimepicker({
            format: "YYYY-MM-DD HH:mm:ss",
            stepping: 10,
            maxDate: i
        });
    },
    scrollActions: function() {
        $(window).scroll(function() {});
    },
    resizeActions: function() {
        $(window).resize(function() {}).resize();
    },
    ieMessage: function() {
        if (sv.$html.hasClass("lt-ie9")) {
            var i = "<div id='ie' class='container'>";
            i += "<h1>Problema!</h1>", i += "<p class='intro'>Sembra che tu sia usando un browser abbastanza datato.</p>", 
            i += "<p>Browser vecchi, come Internet Explorer versione 6,7 e 8 non sono supportati dal nostro sito, scaricane uno pi√π recente ai seguenti link:</p>", 
            i += "<ul>", i += "<li><a href='http://www.google.com/chrome' target='_blank'>Google Chrome</a></li>", 
            i += "<li><a href='http://www.mozilla.org/en-US/firefox/new/' target='_blank'>Firefox</a></li>", 
            i += "<li><a href='http://www.apple.com/safari/'' target='_blank'>Safari</a></li>", 
            i += "</ul></div>", $(".main").replaceWith(i);
        }
    }
};

jQuery(document).ready(function(i) {
    ScriptsVari.init();
});